 <?phpinclude('../newconf.php');if(!isset($_REQUEST['usern'])) exit;else {//interroga db, si suppone che user e pwd siano stati inseriti correttamente$dbh=connect();$sql="SELECT * FROM utente WHERE utente = '{$_REQUEST['usern']}'"; //scrive tutte le informazioni dell'utente try{global $dbh;$result = $dbh->query($sql);$result = $result->fetch(PDO::FETCH_ASSOC);if($result==null or $result=='' ) throw new Exception("Error Processing Request", 1);write($result);} catch (PDOException $exc) { print "<br>Error2!: " . $exc->getMessage() . "<br/>";  die(); }}function write($lungo){$lungo= str_replace("&","E",$lungo);$lungo= str_replace(">","E",$lungo);$lungo= str_replace("<","E",$lungo);foreach ($lungo as $key=>$value)			if ($lungo[$key]==" " || $lungo[$key]=="") $lungo[$key]="empty"; 			header('Content-Type: text/html');$string="<voce><utente>{$lungo['utente']}</utente><nome>{$lungo['nome']}</nome><attivo>{$lungo['attivo']}</attivo><password>{$lungo['password']}</password><cognome>{$lungo['cognome']}</cognome><nick>{$lungo['utente']}</nick><pass>{$lungo['password']}</pass><indirizzo>{$lungo['indirizzo']}</indirizzo><citta>{$lungo['citta']}</citta><cap>{$lungo['cap']}</cap><provincia>{$lungo['provincia']}</provincia><telefono>{$lungo['telefono']}</telefono><fax>{$lungo['cellulare']}</fax><nazione>{$lungo['nazione']}</nazione><mail>{$lungo['email']}</mail><paga>{$lungo['tipo_pagamento']}</paga><conto>{$lungo['numeroconto']}</conto><cfisc>{$lungo['codice_fiscale']}</cfisc><datanascita>{$lungo['datanascita']}</datanascita><luogonascita>{$lungo['luogonascita']}</luogonascita><abicab>{$lungo['abicab']}</abicab><settore>{$lungo['settore']}</settore><notapermanente>{$lungo['notapermanente']}</notapermanente><categoria>{$lungo['categoria']}</categoria><zona>{$lungo['zona']}</zona><codice_sdi>{$lungo['codice_sdi']}</codice_sdi><ind_pec>{$lungo['ind_pec']}</ind_pec></voce>";echo $string;}?>